select:
select * from emp where

        name like concat('%', #{name}, '%')


        and gender = #{gender}


        and entrydate between #{begin} and #{end}

    and entrydate bewteen #{begin} and #{end}

select.where:

    name like concat('%', #{name}, '%')


    and gender = #{gender}


    and entrydate between #{begin} and #{end}

